import { useState } from 'react'; 

let isX = true; 
let stateArray = []; 
let n = 5; 
for (let i = 0; i < n; i++) {
  stateArray.push([]); 
  for (let j = 0; j < n; j++) {
    stateArray[i].push(""); 
  }
}

function Square({label}) {

  let rowIndex = Math.floor(label / n); 
  let colIndex = label % n; 

  const [value, setValue] = useState(null); 
  // call useState from component
  // allows component to "remember things"
  // - value: stores value
  // - setValue: function used to change the value
  // - null is the initial state for value

  function checkWin() {
    // check rows
    for (let i = 0; i < n; i++) {
      // if first in a row is empty, check next row
      if (stateArray[i][0] == '') continue; 
      let charToCheck = stateArray[i][0]; // remember what you're checking for (X or O)
      for (let j = 0; j < n; j++) {
        if (stateArray[i][j] != charToCheck) break; // if not equal to what you're checking, check next row
        if (j == n - 1 && stateArray[i][j] == charToCheck) return charToCheck; // if on last column + equal to char to check, return that char
      }
    }
    // check cols
    for (let i = 0; i < n; i++) {
      // if first in a col is empty, check next col
      if (stateArray[i][0] == '') continue; 
      let charToCheck = stateArray[i][0]; // remember what you're checking for (X or O)
      for (let j = 0; j < n; j++) {
        if (stateArray[i][j] != charToCheck) break; // if not equal to what you're checking, check next row
        if (j == n - 1 && stateArray[i][j] == charToCheck) return charToCheck; // if on last column + equal to char to check, return that char
      }
    }
    // check diagonals
    // top left to bottom right
    if (stateArray[0][0] == stateArray[1][1] && 
      stateArray[0][0] == stateArray[2][2]) {
        if (stateArray[0][0] == 'X') return 'X'; 
        else if (stateArray[0][0] == 'O') return 'O'
    }
    // top right to bottom left
    if (stateArray[0][2] == stateArray[1][1] && 
      stateArray[0][2] == stateArray[2][0]) {
        if (stateArray[0][2] == 'X') return 'X'; 
        else if (stateArray[0][2] == 'O') return 'O'
    }
  }

  function handleClick() {
    if (isX && stateArray[rowIndex][colIndex] == '') {
      setValue('X'); // sets value var. to X
      isX = false; 
      stateArray[rowIndex][colIndex] = 'X'; 

      if (checkWin() == "X") {
        alert("X Wins"); 
      }
      else if (checkWin() == "O") {
        alert("O Wins"); 
      }
    } else if (!isX && stateArray[rowIndex][colIndex] == '') {
      setValue('O'); // sets value var. to X
      isX = true; 
      stateArray[rowIndex][colIndex] = 'O'; 

      if (checkWin() == "X") {
        alert("X Wins"); 
      }
      else if (checkWin() == "O") {
       alert("O Wins"); 
      }
    } 
  }

  return (
    <button 
      className="square"
      onClick={handleClick}
    >
      {value}
    </button>
  ); 
}

export default function Board() {
  return (
    <>
    <div className="board-row"> 
      <Square label="0"/>
      <Square label="1"/>
      <Square label="2"/>
    </div> 
    <div className="board-row"> 
      <Square label="3"/>
      <Square label="4"/>
      <Square label="5"/>
    </div> 
    <div className="board-row"> 
      <Square label="6"/>
      <Square label="7"/>
      <Square label="8"/>
    </div> 
    </>
  ); 

}
