import { useState } from 'react'; 

let isX = true; 
let stateArray = []; 
for (let i = 0; i < 3; i++) {
  stateArray.push([]); 
  for (let j = 0; j < 3; j++) {
    stateArray[i].push(""); 
  }
}

function Square({label}) {
  let rowIndex = Math.floor(label / 3); 
  let colIndex = label % 3; 

  const [value, setValue] = useState(null); 
  // call useState from component
  // allows component to "remember things"
  // - value: stores value
  // - setValue: function used to change the value
  // - null is the initial state for value

  function isXWin() {
    // check rows
    for (let i = 0; i < 3; i++) {
      if (stateArray[i][0] == '') continue; 
      for (let j = 0; j < 2; j++) {
        if (stateArray[i][j] != stateArray[i][j + 1]) {
          break; 
        }
        if (j == 1) {
          if (stateArray[i][j] == 'X') return 'X'; 
          else return 'O'; 
        }
      }
    }
    // check cols
    for (let i = 0; i < 3; i++) {
      if (stateArray[0][i] == '') continue; 
      for (let j = 0; j < 2; j++) {
        if (stateArray[j][i] != stateArray[j + 1][i]) {
          break; 
        }
        if (j == 1) {
          if (stateArray[j][i] == 'X') return 'X'; 
          else return 'O';
        }
      }
    }
    // check diagonals 
    if (stateArray[0][0] == '' || stateArray[0][2] == '') return; 
    for (let i = 0; i < 3; i++) {
      if (stateArray[i][i] != stateArray[i + 1][i + 1]) {
        break; 
      }
      if (i == 1) {
        if (stateArray[i][i] == 'X') return 'X'; 
        else return 'O'; 
      }
    }
    for (let i = 2; i >= 0; i--) {
      if (stateArray[i][i] != stateArray[i - 1][i - 1]) {
        break; 
      }
      if (i == 1) {
        if (stateArray[i][i] == 'X') return 'X'; 
        else return 'O'; 
      } 
    }
    return; 
  }

  function handleClick() {
    if (isX && stateArray[rowIndex][colIndex] == '') {
      setValue('X'); // sets value var. to X
      isX = false; 
      stateArray[rowIndex][colIndex] = 'X'; 
      if (isXWin) alert("X WINS!"); 
      else if (!isXWin) alert ("O WINS!"); 
    } else if (!isX && stateArray[rowIndex][colIndex] == '') {
      setValue('O'); // sets value var. to X
      isX = true; 
      stateArray[rowIndex][colIndex] = 'O'; 
      if (isXWin) alert("X WINS!"); 
      else if (!isXWin) alert ("O WINS!"); 
    }
    console.log(stateArray); 
  }

  return (
    <button 
      className="square"
      onClick={handleClick}
    >
      {value}
    </button>
  ); 
}

export default function Board() {
  return (
    <>
    <div className="board-row"> 
      <Square label="0"/>
      <Square label="1"/>
      <Square label="2"/>
    </div> 
    <div className="board-row"> 
      <Square label="3"/>
      <Square label="4"/>
      <Square label="5"/>
    </div> 
    <div className="board-row"> 
      <Square label="6"/>
      <Square label="7"/>
      <Square label="8"/>
    </div> 
    </>
  ); 

}
