import { useState } from 'react'; 

let isX = true; 
let stateArray = []; 
for (let i = 0; i < 3; i++) {
  stateArray.push([]); 
  for (let j = 0; j < 3; j++) {
    stateArray[i].push(""); 
  }
}

function Square({label}) {

  const [value, setValue] = useState(null); 
  // call useState from component
  // allows component to "remember things"
  // - value: stores value
  // - setValue: function used to change the value
  // - null is the initial state for value

  function handleClick() {
    if (isX) {
      setValue('X'); // sets value var. to X
      isX = false; 
    } else {
      setValue('O'); // sets value var. to X
      isX = true; 
    }
  }
  console.log(label); 

  return (
    <button 
      className="square"
      onClick={handleClick}
    >
      {value}
    </button>
  ); 
}

export default function Board() {
  return (
    <>
    <div className="board-row"> 
      <Square label="1"/>
      <Square label="2"/>
      <Square label="3"/>
    </div> 
    <div className="board-row"> 
      <Square label="4"/>
      <Square label="5"/>
      <Square label="6"/>
    </div> 
    <div className="board-row"> 
      <Square label="7"/>
      <Square label="8"/>
      <Square label="9"/>
    </div> 
    </>
  ); 

}
