import { useState } from 'react'; 

let isX = true; 
let stateArray = []; 
for (let i = 0; i < 3; i++) {
  stateArray.push([]); 
  for (let j = 0; j < 3; j++) {
    stateArray[i].push(""); 
  }
}

function Square({label}) {
  let rowIndex = Math.floor(label / 3); 
  let colIndex = label % 3; 

  const [value, setValue] = useState(null); 
  // call useState from component
  // allows component to "remember things"
  // - value: stores value
  // - setValue: function used to change the value
  // - null is the initial state for value

  function checkWin() {
    // check rows
    for (let i = 0; i < 3; i++) {
      if (stateArray[i][0] == '') continue; 

      if (stateArray[i][0] == stateArray[i][1] && 
        stateArray[i][0] == stateArray[i][2]) {

        if (stateArray[i][0] == 'X') return 'X'; 
        else if (stateArray[i][0] == 'O') return 'O'

      }
    }
    // check cols
    for (let i = 0; i < 3; i++) {
      if (stateArray[0][i] == '') continue; 

      if (stateArray[0][i] == stateArray[1][i] && 
        stateArray[0][i] == stateArray[2][i]) {

        if (stateArray[0][i] == 'X') return 'X'; 
        else if (stateArray[0][i] == 'O') return 'O'

      }
    }
    // check diagonals
    // top left to bottom right
    if (stateArray[0][0] == stateArray[1][1] && 
      stateArray[0][0] == stateArray[2][2]) {
        if (stateArray[0][0] == 'X') return 'X'; 
        else if (stateArray[0][0] == 'O') return 'O'
    }
    // top right to bottom left
    if (stateArray[0][2] == stateArray[1][1] && 
      stateArray[0][2] == stateArray[2][0]) {
        if (stateArray[0][2] == 'X') return 'X'; 
        else if (stateArray[0][2] == 'O') return 'O'
    }
  }

  function handleClick() {
    if (isX && stateArray[rowIndex][colIndex] == '') {
      setValue('X'); // sets value var. to X
      isX = false; 
      stateArray[rowIndex][colIndex] = 'X'; 

      if (checkWin() == "X") {
        console.log("X Wins"); 
      }
      else if (checkWin() == "O") {
        console.log("O Wins"); 
      }
    } else if (!isX && stateArray[rowIndex][colIndex] == '') {
      setValue('O'); // sets value var. to X
      isX = true; 
      stateArray[rowIndex][colIndex] = 'O'; 

      if (checkWin() == "X") {
        console.log("X Wins"); 
      }
      else if (checkWin() == "O") {
       console.log("O Wins"); 
      }
    }
    console.log(stateArray); 
  }

  return (
    <button 
      className="square"
      onClick={handleClick}
    >
      {value}
    </button>
  ); 
}

export default function Board() {
  return (
    <>
    <div className="board-row"> 
      <Square label="0"/>
      <Square label="1"/>
      <Square label="2"/>
    </div> 
    <div className="board-row"> 
      <Square label="3"/>
      <Square label="4"/>
      <Square label="5"/>
    </div> 
    <div className="board-row"> 
      <Square label="6"/>
      <Square label="7"/>
      <Square label="8"/>
    </div> 
    </>
  ); 

}
